using Microsoft.AspNetCore.Mvc;

namespace CSharpControllers.Output.Controllers {
  [Route("api/v1/Smidgets"), ApiController]
  public class Smidgets : Controller {
    private readonly EntityDbContext _db;

    public Smidgets(EntityDbContext db) {
      _db = db;
    }


    [HttpGet("/:id")]
    public IActionResult<List<Smidget>> GetAll([FromRoute] Guid id) {
      return Ok(_db.Smidgets.Single(x => x.Id.Equals(id)));
    }

  }
}